query:
mutation {
  addProduct(input: { name: "new", price: 599.99 }) {
    name
    price
    isExpensive
    isAvailable
  }
}

plan:
QueryPlan {
  Sequence {
    Fetch(service: "a") {
      {
        addProduct(input: {name: "new", price: 599.99}) {
          __typename
          id
          name
          price
        }
      }
    },
    Flatten(path: "addProduct") {
      Fetch(service: "b") {
        {
          ... on Product {
            __typename
            id
            price
          }
        } =>
        {
          ... on Product {
            isExpensive
            isAvailable
          }
        }
      },
    },
  },
}

